import { rest } from 'msw';

// Mock data for appointments
const mockAppointments = [
  {
    _id: '1',
    patientId: {
      _id: 'patient123',
      name: 'Alex Johnson',
      email: 'alex@example.com',
      phone: '123-456-7890'
    },
    doctorId: {
      _id: 'doctor456',
      name: 'Dr. Sarah Parker',
      specialization: 'Cardiology',
      email: 'dr.parker@example.com'
    },
    date: new Date(Date.now() + 86400000 * 2).toISOString(), // 2 days from now
    timeSlot: '10:00 AM',
    status: 'scheduled',
    reason: 'Annual checkup',
    notes: ''
  },
  {
    _id: '2',
    patientId: {
      _id: 'patient123',
      name: 'Alex Johnson',
      email: 'alex@example.com',
      phone: '123-456-7890'
    },
    doctorId: {
      _id: 'doctor789',
      name: 'Dr. Michael Chen',
      specialization: 'Neurology',
      email: 'dr.chen@example.com'
    },
    date: new Date(Date.now() - 86400000 * 3).toISOString(), // 3 days ago
    timeSlot: '2:00 PM',
    status: 'completed',
    reason: 'Headaches and dizziness',
    notes: 'Patient reported recurring headaches. Prescribed medication and recommended follow-up in 2 weeks.'
  }
];

// Mock available time slots
const mockTimeSlots = {
  availableTimeSlots: [
    '09:00 AM', '10:00 AM', '11:00 AM',
    '01:00 PM', '02:00 PM', '03:00 PM', '04:00 PM'
  ]
};

export const handlers = [
  // Get user appointments
  rest.get('/api/appointments/user', (req, res, ctx) => {
    return res(
      ctx.status(200),
      ctx.json({ appointments: mockAppointments })
    );
  }),

  // Get available appointment slots
  rest.get('/api/appointments/available-slots', (req, res, ctx) => {
    return res(
      ctx.status(200),
      ctx.json(mockTimeSlots)
    );
  }),

  // Book appointment
  rest.post('/api/appointments', (req, res, ctx) => {
    const { doctorId, date, timeSlot, reason } = req.body;
    
    const newAppointment = {
      _id: Math.random().toString(36).substr(2, 9),
      patientId: {
        _id: 'patient123',
        name: 'Alex Johnson',
        email: 'alex@example.com',
        phone: '123-456-7890'
      },
      doctorId: {
        _id: doctorId,
        name: 'Dr. Selected Doctor',
        specialization: 'General Medicine'
      },
      date,
      timeSlot,
      status: 'scheduled',
      reason,
      notes: ''
    };
    
    mockAppointments.push(newAppointment);
    
    return res(
      ctx.status(201),
      ctx.json({
        message: 'Appointment booked successfully',
        appointment: newAppointment
      })
    );
  }),

  // Cancel appointment
  rest.delete('/api/appointments/:id', (req, res, ctx) => {
    const { id } = req.params;
    const appointmentIndex = mockAppointments.findIndex(apt => apt._id === id);
    
    if (appointmentIndex === -1) {
      return res(
        ctx.status(404),
        ctx.json({ message: 'Appointment not found' })
      );
    }
    
    mockAppointments[appointmentIndex].status = 'cancelled';
    
    return res(
      ctx.status(200),
      ctx.json({
        message: 'Appointment cancelled successfully',
        appointment: mockAppointments[appointmentIndex]
      })
    );
  })
];
