import React, { createContext, useState, useEffect, useContext } from 'react';

const AuthContext = createContext();

export const useAuth = () => useContext(AuthContext);

export const AuthProvider = ({ children }) => {
  const [currentUser, setCurrentUser] = useState(null);
  const [token, setToken] = useState(localStorage.getItem('token'));
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    // Check if there's a token in localStorage and fetch user data
    const fetchUser = async () => {
      if (token) {
        try {
          const response = await fetch('/api/users/me', {
            headers: {
              Authorization: `Bearer ${token}`
            }
          });

          if (response.ok) {
            const userData = await response.json();
            setCurrentUser(userData);
          } else {
            // Token might be invalid or expired
            logout();
          }
        } catch (error) {
          console.error('Error fetching user data:', error);
          setError('Failed to authenticate user');
          logout();
        }
      }
      setLoading(false);
    };

    fetchUser();
  }, [token]);

  // Login function with role parameter
  const login = async (email, password, role) => {
    setLoading(true);
    setError(null);
    
    try {
      const response = await fetch('/api/users/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({ email, password, role })
      });

      const data = await response.json();

      if (response.ok) {
        localStorage.setItem('token', data.token);
        setToken(data.token);
        
        // Store user role in localStorage for persistence
        localStorage.setItem('userRole', data.user.role);
        
        // Set current user with data from response
        setCurrentUser(data.user);
        
        return true;
      } else {
        // Check if the error is related to incorrect role
        if (data.code === 'ROLE_MISMATCH') {
          setError('The selected role does not match your account type. Please select the correct role.');
        } else {
          setError(data.msg || 'Login failed');
        }
        return false;
      }
    } catch (error) {
      console.error('Login error:', error);
      setError('An error occurred during login');
      return false;
    } finally {
      setLoading(false);
    }
  };

  // Register function
  const register = async (userData) => {
    setLoading(true);
    setError(null);
    
    try {
      const response = await fetch('/api/users/register', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(userData)
      });

      const data = await response.json();

      if (response.ok) {
        localStorage.setItem('token', data.token);
        setToken(data.token);
        
        // Store user role in localStorage for persistence
        localStorage.setItem('userRole', data.user.role);
        
        // Set current user with data from response
        setCurrentUser(data.user);
        
        return true;
      } else {
        setError(data.msg || 'Registration failed');
        return false;
      }
    } catch (error) {
      console.error('Registration error:', error);
      setError('An error occurred during registration');
      return false;
    } finally {
      setLoading(false);
    }
  };

  // Logout function
  const logout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('userRole');
    setToken(null);
    setCurrentUser(null);
  };

  // Helper function for authenticated API requests
  const authFetch = async (url, options = {}) => {
    if (!token) {
      throw new Error('No authentication token');
    }

    const authOptions = {
      ...options,
      headers: {
        ...options.headers,
        Authorization: `Bearer ${token}`
      }
    };

    const response = await fetch(url, authOptions);
    
    if (response.status === 401) {
      // Token expired or invalid
      logout();
      throw new Error('Session expired. Please login again.');
    }
    
    return response;
  };

  const value = {
    currentUser,
    loading,
    error,
    login,
    register,
    logout,
    authFetch
  };

  return <AuthContext.Provider value={value}>{children}</AuthContext.Provider>;
};

export default AuthContext;
