import React, { useState } from 'react';
import CalendarComponent from '../components/CalendarComponent';

const AppointmentForm = () => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    phone: '',
    reason: '',
    selectedDate: new Date(),
    selectedTimeSlot: null,
  });
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [showConfirmation, setShowConfirmation] = useState(false);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prevState => ({
      ...prevState,
      [name]: value
    }));
  };

  const handleDateSelect = (date) => {
    setFormData(prevState => ({
      ...prevState,
      selectedDate: date,
      selectedTimeSlot: null
    }));
  };

  const handleTimeSlotSelect = (timeSlot) => {
    setFormData(prevState => ({
      ...prevState,
      selectedTimeSlot: timeSlot
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsSubmitting(true);

    try {
      // Simulate API call to book appointment
      await new Promise(resolve => setTimeout(resolve, 1000));
      
      // Show confirmation on success
      setShowConfirmation(true);
    } catch (error) {
      alert('Failed to book appointment. Please try again.');
    } finally {
      setIsSubmitting(false);
    }
  };

  if (showConfirmation) {
    return (
      <div className="confirmation-page">
        <h2>Appointment Confirmed!</h2>
        <div className="confirmation-details">
          <p><strong>Patient:</strong> {formData.name}</p>
          <p><strong>Date:</strong> {formData.selectedDate.toDateString()}</p>
          <p><strong>Time:</strong> {formData.selectedTimeSlot}</p>
          <p><strong>Reason:</strong> {formData.reason}</p>
          <p>A confirmation has been sent to {formData.email}</p>
        </div>
        <button onClick={() => setShowConfirmation(false)}>Book Another Appointment</button>
      </div>
    );
  }

  return (
    <div className="appointment-form">
      <h1>Book an Appointment</h1>
      
      <CalendarComponent 
        onDateSelect={handleDateSelect}
        onTimeSlotSelect={handleTimeSlotSelect}
        selectedDate={formData.selectedDate}
        selectedTimeSlot={formData.selectedTimeSlot}
      />

      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label htmlFor="name">Full Name</label>
          <input 
            type="text" 
            id="name" 
            name="name" 
            value={formData.name} 
            onChange={handleInputChange} 
            required 
          />
        </div>

        <div className="form-group">
          <label htmlFor="email">Email</label>
          <input 
            type="email" 
            id="email" 
            name="email" 
            value={formData.email} 
            onChange={handleInputChange} 
            required 
          />
        </div>

        <div className="form-group">
          <label htmlFor="phone">Phone Number</label>
          <input 
            type="tel" 
            id="phone" 
            name="phone" 
            value={formData.phone} 
            onChange={handleInputChange} 
            required 
          />
        </div>

        <div className="form-group">
          <label htmlFor="reason">Reason for Visit</label>
          <textarea 
            id="reason" 
            name="reason" 
            value={formData.reason} 
            onChange={handleInputChange} 
            required 
          />
        </div>

        <button 
          type="submit" 
          disabled={isSubmitting || !formData.selectedTimeSlot}
        >
          {isSubmitting ? 'Booking...' : 'Book Appointment'}
        </button>
      </form>
    </div>
  );
};

export default AppointmentForm;
