import React, { useState, useEffect } from 'react';
import { useAuth } from '../context/AuthContext';
import { useNavigate } from 'react-router-dom';
import AppointmentsList from '../components/AppointmentsList';

const PatientDashboard = () => {
  const { currentUser, logout } = useAuth();
  const navigate = useNavigate();
  const [activeTab, setActiveTab] = useState('appointments');
  const [showLogoutConfirm, setShowLogoutConfirm] = useState(false);

  // Redirect if user is not logged in or not a patient
  useEffect(() => {
    if (!currentUser) {
      return;
    }
    
    if (currentUser.role !== 'patient') {
      navigate('/');
    }
  }, [currentUser, navigate]);

  const handleLogoutClick = () => {
    setShowLogoutConfirm(true);
  };

  const handleConfirmLogout = () => {
    logout();
    navigate('/login');
  };

  const handleCancelLogout = () => {
    setShowLogoutConfirm(false);
  };

  if (!currentUser) {
    return <div className="loading">Loading dashboard...</div>;
  }

  if (currentUser.role !== 'patient') {
    return <div className="error-message">Only patients can access this dashboard.</div>;
  }

  return (
    <div className="patient-dashboard">
      <div className="dashboard-header">
        <h1>Patient Dashboard</h1>
        <div className="patient-info">
          <span className="patient-name">{currentUser.name}</span>
          <button className="logout-button" onClick={handleLogoutClick}>
            Logout
          </button>
        </div>
      </div>
      
      {showLogoutConfirm && (
        <div className="logout-confirm-overlay">
          <div className="logout-confirm-dialog">
            <h3>Confirm Logout</h3>
            <p>Are you sure you want to log out?</p>
            <div className="logout-buttons">
              <button className="cancel-button" onClick={handleCancelLogout}>
                No, Stay Logged In
              </button>
              <button className="confirm-logout-button" onClick={handleConfirmLogout}>
                Yes, Log Out
              </button>
            </div>
          </div>
        </div>
      )}
      
      <div className="dashboard-content">
        <div className="dashboard-tabs">
          <button 
            className={`tab-button ${activeTab === 'appointments' ? 'active' : ''}`} 
            onClick={() => setActiveTab('appointments')}
          >
            My Appointments
          </button>
          <button 
            className={`tab-button ${activeTab === 'profile' ? 'active' : ''}`} 
            onClick={() => setActiveTab('profile')}
          >
            My Profile
          </button>
          <button 
            className={`tab-button ${activeTab === 'medical' ? 'active' : ''}`} 
            onClick={() => setActiveTab('medical')}
          >
            Medical Records
          </button>
        </div>
        
        <div className="dashboard-tab-content">
          {activeTab === 'appointments' && (
            <div className="appointments-tab">
              <div className="tab-actions">
                <button 
                  className="book-appointment-button"
                  onClick={() => navigate('/book-appointment')}
                >
                  Book New Appointment
                </button>
              </div>
              <AppointmentsList />
            </div>
          )}
          
          {activeTab === 'profile' && (
            <div className="profile-tab">
              <h2>My Profile</h2>
              <div className="profile-card">
                <div className="profile-info">
                  <div className="info-row">
                    <span className="label">Name:</span>
                    <span className="value">{currentUser.name}</span>
                  </div>
                  <div className="info-row">
                    <span className="label">Email:</span>
                    <span className="value">{currentUser.email}</span>
                  </div>
                  <div className="info-row">
                    <span className="label">Phone:</span>
                    <span className="value">{currentUser.phone}</span>
                  </div>
                  {currentUser.emergencyContact && (
                    <>
                      <h3>Emergency Contact</h3>
                      <div className="info-row">
                        <span className="label">Name:</span>
                        <span className="value">{currentUser.emergencyContact.name}</span>
                      </div>
                      <div className="info-row">
                        <span className="label">Phone:</span>
                        <span className="value">{currentUser.emergencyContact.phone}</span>
                      </div>
                      <div className="info-row">
                        <span className="label">Relationship:</span>
                        <span className="value">{currentUser.emergencyContact.relationship}</span>
                      </div>
                    </>
                  )}
                </div>
                <button className="edit-profile-button">
                  Edit Profile
                </button>
              </div>
            </div>
          )}
          
          {activeTab === 'medical' && (
            <div className="medical-tab">
              <h2>Medical Records</h2>
              {currentUser.medicalHistory && currentUser.medicalHistory.length > 0 ? (
                <div className="medical-records-list">
                  {currentUser.medicalHistory.map((record, index) => (
                    <div key={index} className="medical-record-card">
                      <h3>{record.title}</h3>
                      <p>{record.description}</p>
                      <span className="record-date">{new Date(record.date).toLocaleDateString()}</span>
                    </div>
                  ))}
                </div>
              ) : (
                <div className="no-records">
                  <p>No medical records available.</p>
                </div>
              )}
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default PatientDashboard;
