import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import '../styles/Login.css';

const PhoneLogin = () => {
  const [isPhoneStep, setIsPhoneStep] = useState(true);
  const [phone, setPhone] = useState('');
  const [otp, setOtp] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [countdown, setCountdown] = useState(0);
  
  const { login, currentUser } = useAuth();
  const navigate = useNavigate();
  
  useEffect(() => {
    // Redirect if already logged in
    if (currentUser) {
      switch (currentUser.role) {
        case 'doctor':
          navigate('/doctor-dashboard');
          break;
        case 'patient':
          navigate('/patient-dashboard');
          break;
        default:
          navigate('/dashboard');
      }
    }
  }, [currentUser, navigate]);
  
  useEffect(() => {
    let timer;
    if (countdown > 0) {
      timer = setTimeout(() => setCountdown(countdown - 1), 1000);
    }
    return () => clearTimeout(timer);
  }, [countdown]);
  
  const handlePhoneSubmit = async (e) => {
    e.preventDefault();
    setError('');
    
    // Simple validation
    if (!phone) {
      return setError('Please enter your phone number');
    }
    
    try {
      setLoading(true);
      
      const response = await fetch('/api/auth/send-otp', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ phone })
      });
      
      const data = await response.json();
      
      if (!response.ok) {
        throw new Error(data.message || 'Error sending OTP');
      }
      
      setIsPhoneStep(false);
      setCountdown(60); // 60 seconds countdown for resend
      
    } catch (err) {
      setError(err.message || 'Something went wrong. Please try again.');
    } finally {
      setLoading(false);
    }
  };
  
  const handleOtpSubmit = async (e) => {
    e.preventDefault();
    setError('');
    
    // Simple validation
    if (!otp) {
      return setError('Please enter the OTP sent to your phone');
    }
    
    try {
      setLoading(true);
      
      const response = await fetch('/api/auth/verify-otp', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ phone, otp })
      });
      
      const data = await response.json();
      
      if (!response.ok) {
        throw new Error(data.message || 'Invalid OTP');
      }
      
      // Store token and user info
      login(data.token, data.user);
      
      // Redirect based on role
      if (data.user.role === 'doctor') {
        navigate('/doctor-dashboard');
      } else if (data.user.role === 'patient') {
        navigate('/patient-dashboard');
      } else {
        navigate('/dashboard');
      }
      
    } catch (err) {
      setError(err.message || 'Something went wrong. Please try again.');
    } finally {
      setLoading(false);
    }
  };
  
  const handleResendOTP = async () => {
    if (countdown > 0) return;
    
    try {
      setLoading(true);
      
      const response = await fetch('/api/auth/send-otp', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ phone })
      });
      
      const data = await response.json();
      
      if (!response.ok) {
        throw new Error(data.message || 'Error sending OTP');
      }
      
      setCountdown(60); // Reset countdown
      
    } catch (err) {
      setError(err.message || 'Something went wrong. Please try again.');
    } finally {
      setLoading(false);
    }
  };
  
  return (
    <div className="login-page">
      <div className="login-container">
        <div className="login-header">
          <h2>{isPhoneStep ? 'Phone Login' : 'Verify OTP'}</h2>
        </div>
        
        {error && <div className="error-alert">{error}</div>}
        
        {isPhoneStep ? (
          <form className="login-form" onSubmit={handlePhoneSubmit}>
            <div className="form-group">
              <label htmlFor="phone">Phone Number</label>
              <input
                type="tel"
                id="phone"
                value={phone}
                onChange={(e) => setPhone(e.target.value)}
                placeholder="Enter your phone number"
              />
            </div>
            
            <button 
              type="submit" 
              className="login-button" 
              disabled={loading}
            >
              {loading ? 'Sending OTP...' : 'Send OTP'}
            </button>
            
            <div className="login-footer">
              <p>
                Remember your password? <Link to="/login">Login with Email</Link>
              </p>
              <p>
                Don't have an account? <Link to="/register">Register</Link>
              </p>
            </div>
          </form>
        ) : (
          <form className="login-form" onSubmit={handleOtpSubmit}>
            <p className="otp-info">
              A verification code has been sent to {phone}
            </p>
            
            <div className="form-group">
              <label htmlFor="otp">Verification Code</label>
              <input
                type="text"
                id="otp"
                value={otp}
                onChange={(e) => setOtp(e.target.value)}
                placeholder="Enter 6-digit OTP"
                maxLength={6}
              />
            </div>
            
            <button 
              type="submit" 
              className="login-button" 
              disabled={loading}
            >
              {loading ? 'Verifying...' : 'Verify & Login'}
            </button>
            
            <div className="resend-otp">
              {countdown > 0 ? (
                <p>Resend OTP in {countdown} seconds</p>
              ) : (
                <button 
                  type="button" 
                  onClick={handleResendOTP} 
                  className="resend-button"
                  disabled={loading}
                >
                  Resend OTP
                </button>
              )}
            </div>
            
            <div className="login-footer">
              <button 
                type="button" 
                className="back-button"
                onClick={() => setIsPhoneStep(true)}
              >
                Back to Phone Entry
              </button>
            </div>
          </form>
        )}
      </div>
    </div>
  );
};

export default PhoneLogin;
